CREATE TABLE `articles` (
  `id` int(11) NOT NULL COMMENT '主键',
  `title` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章内容',
  `abstract_is` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否有摘要',
  `abstract` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '文章摘要',
  `author_id` int(11) NOT NULL COMMENT '作者id',
  `title_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '文章封面',
  `cate_id` int(4) DEFAULT NULL COMMENT '所属类别',
  `cate_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `pv` int(255) NOT NULL DEFAULT '0' COMMENT '阅读量',
  `create_time` int(20) DEFAULT NULL COMMENT '创建时间',
  `update_time` int(20) DEFAULT NULL COMMENT '更新时间'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

